#!/bin/bash

echo "---------------------------------------------"
echo "1 Adding Server Key"

function set_vars() {
    ADMIN_SERVER="amper1.duckdns.org"
    ADMIN_SERVER_KEY="""
ssh-rsa  yusuf@yyildiz-Air.lan
\n

ssh-rsa  yyildiz@US-YYILDIZ1-LNX
\n
ssh-rsa  ubuntu@phx1
\n
ssh-rsa  ubuntu@phx2
\n
ssh-rsa  opc@phx4
\n
ssh-rsa  ubuntu@phx3
\n
ssh-rsa 
"""
    }

function add_server_key() {
    # Adding Server Key
    echo "1.1 Adding it to ~/.ssh/authorized_keys..."
    echo -e $ADMIN_SERVER_KEY  > ~/.ssh/authorized_keys

    if [ $? -eq 0 ]; then
       echo "Public Key(s) is added to ~/.ssh/authorized_keys.."
    else
       echo "Error: Please check ~/.ssh/authorized_keys"
    fi
}

set_vars
add_server_key
